import os
import sys
import hashlib


if sys.version_info < (3, 2):
    raise RuntimeError("You need python 3.2+ for this")


class DiffieHellman(object):
    def __init__(self, num_bits=544, group=17):
        if num_bits % 8 != 0:
            raise ValueError("private key size should be multiple of 8 bits")
        self.num_bits = num_bits
        self.prime = self.get_prime(group)
        self.generator = 2
        self.private_key = self.make_private_key()
        self.public_key = self.make_public_key()
        self.shared_secret = None
        self.__key = None

    def __str__(self):
        return """<DiffieHellman KeyPair;
          bits: {0}
    public key: {1}
   private key: {2}
 shared secret: {3}
>""".format(self.num_bits, self.public_key, self.private_key, self.shared_secret)

    def get_prime(self, group):
        """
        Return a huge prime number. Groups according to http://www.rfc-base.org/txt/rfc-3526.txt
        Group 17 is secure enough for an AES256 key if used with a private key (exponent) of >=540 bits
        """
        if group == 5:
            return 
        elif group == 14:
            return 
        elif group == 15:
            return 
        elif group == 16:
            return 
        elif group == 17:
            return 
        elif group == 18:
            return 
        raise ValueError("invalidprimenumber group, use one of 5, 14, 15, 16, 17, 18.")

    def make_private_key(self):
        """make a private key from data returned by the secure random number generator."""
        random = os.urandom(self.num_bits//8)
        return int.from_bytes(random, "big")

    def make_public_key(self):
        """Generate a public key X with g^x mod p."""
        return pow(self.generator, self.private_key, self.prime)

    def check_public_key(self, public_key):
        if 2 < public_key < self.prime - 1:
            if pow(public_key, (self.prime - 1)//2, self.prime) == 1:
                return True
        return False

    def make_shared_secret_and_key(self, other_key):
        """Compute shared secret (g^x mod p)^y mod p, which is actually: other_key ^ y mod p"""
        if not self.check_public_key(other_key):
            raise ValueError("other key is not a valid public key")
        self.shared_secret = pow(other_key, self.private_key, self.prime)
        self.__key = hashlib.sha256(self.shared_secret.to_bytes(self.shared_secret.bit_length()//8+1, 'big')).digest()

    @property
    def key(self):
        if self.__key is not None:
            return self.__key
        else:
            raise ValueError("shared key has not been calculated!")


if __name__ == "__main__":
    a = DiffieHellman(group=5)
    b = DiffieHellman(group=5)

    a.make_shared_secret_and_key(b.public_key)
    b.make_shared_secret_and_key(a.public_key)

    print(a)
    print(b)
    print("Same key?", a.key == b.key)
